//JavaScript
let assets = [];
const containerId = 'chart-parameter-container';
const widgetContainer = $(`#${containerId}`);
// debugger;
widgetContainer.empty();
if (!datasources.getHistoryForSensorsB.lastUpdated) {
    // send a bogus query to initialize this datasource with an empty array
    datasources.getHistoryForSensorsB.sendData({
        "sensor_ids": [
            "351530"
        ],
        "startTime": "2042-09-20T18:52:58.971Z",
        "endTime": "2042-09-21T18:52:58.971Z"
    })
}
const handleAssets = (data) => {
    // debugger;
    datasources.getAssets.latestData.unsubscribe(handleAssets);
    assets = data;
    addDropdownsToContainer(createParameterDropdowns(0));
}

const addDropdownsToContainer = (dropdowns) => {
    widgetContainer.append(dropdowns);
}

const removeParamDropdown = (index) => {
    $(`#${paramDropdownContainerIdPrefix}${index}`).remove();
}

const paramDropdownContainerClass = 'parameter-select-container';
const paramDropdownContainerIdPrefix = 'parameter-select-container-id-';
const locationDropdownClass = 'location-select';
const sensorDropdownClass = 'sensor-select';
const createParameterDropdowns = (index) => {
    const badge = $('<div></div>');
    badge.attr('id', `${paramDropdownContainerIdPrefix}${index}`);
    badge.addClass('badge badge-light badge-pill');
    const container = $('<div></div>');
    badge.append(container);
    container.addClass('form-group');
    container.addClass(paramDropdownContainerClass);
    const locationDropdown = createLocationDropdown(index, assets);
    const sensorDropdown = createSensorDropdown(index);
    container.append(locationDropdown);
    container.append(sensorDropdown)
    const removeParamElem = $('<i></i>')
    removeParamElem.addClass('fa fa-times-circle c-pointer remove-param');
    removeParamElem.on('click', () => {
        removeParamDropdown(index);
    })
    container.append(removeParamElem);
    return badge;
}

const changeSensorOptions = (index, sensors) => {
    debugger;
    const sensorDropdown = $(`#${paramDropdownContainerIdPrefix}${index} .${sensorDropdownClass}`);
    sensorDropdown.empty();
    sensorDropdown.append(createBlankSensorItem());
    sensors.forEach((s) => {
        sensorDropdown.append(createDropdownItem(s.name, s.sensor_label));
    });
}

const createBlankSensorItem = () => {
    return createDropdownItem('', '-- select sensor --');
}

const createSensorDropdown = (index) => {
    const sensorDropdown = $('<select></select>');
    sensorDropdown.addClass('form-control-sm')
    sensorDropdown.addClass(sensorDropdownClass);
    sensorDropdown.append(createBlankSensorItem());

    return sensorDropdown;
}

const createDropdownItem = (value, label) => {
    const item = $('<option></option>');
    item.attr('value', value).text(label);
    return item;
}

const createLocationDropdown = (index, locations) => {
    const locationDropdown = $('<select></select>');
    locationDropdown.addClass('form-control-sm')
    locationDropdown.addClass(locationDropdownClass);

    locationDropdown.append(createDropdownItem('blank', '-- select gateway --'))
    locations.forEach((l) => {
        const item = createDropdownItem(l.name, l.name);
        locationDropdown.append(item)
    })

    locationDropdown.on('change', (e) => {
        getSensorsByEdgeId(e.target.value).then((sensors) => {
            console.log('sensors', sensors);
            changeSensorOptions(index, sensors);
        });
    })

    return locationDropdown;
}

const getSensorsByEdgeId = (id) => {
    return datasources.getSensorsByEdgeId.sendData({ edge_id: id }).then((data) => {
        console.log('data', data);
        return data.results;
    })
}

window.addNewParameter = () => {
    const currentNumOfParams = widgetContainer.find(`.${paramDropdownContainerClass}`).length;
    addDropdownsToContainer(createParameterDropdowns(currentNumOfParams))
}

const createHeadersForCSV = (columns) => {
    return columns.map((k) => `${k},`);
}

const generateSensorHistoryCSV = (sensorHistory, allSensors) => {
    try {
        const fields = Object.keys(sensorHistory[sensorHistory.length - 1]).sort((a, b) => {
            if (a === 'timestamp') {
                return -1;
            }
            if (b === 'timestamp') {
                return 1;
            }
            return 0;
        });
        const replacer = function (key, value) { return value === null ? '' : value }
        const csv = sensorHistory.map(function (row) {
            return fields.map(function (fieldName) {
                return JSON.stringify(row[fieldName], replacer)
            }).join(',')
        })
        csv.unshift(getHeadersWithSensorLabels(fields, allSensors).join(',')) // add header column
        return csv.join('\r\n');
    } catch (e) {
        console.error('Unable to create CSV for sensor history', e);
    }
}

const getHeadersWithSensorLabels = (fields, allSensors) => {
    try {
        return fields.map((f) => {
            if (f === 'timestamp') {
                return f;
            } else {
                for (let i=0, len=allSensors.length; i < len; i++) {
                    if (allSensors[i].sensor_id === f) {
                        return allSensors[i].sensor_label;
                    }
                }
                return f;
            }
        })
    } catch (e) {
        return fields;
    }
    return fields;
}

const handleSensorHistory = (resp) => {
    const data = resp.results;
    const exportButton = $('#export-sensor-history');

    datasources.getSensors.sendData({}).then((sensorResp) => {
        const sensors = sensorResp.results.DATA;
        const csv = generateSensorHistoryCSV(data, sensors);
        exportButton.attr('href', `data:text/csv;charset=utf-8,${csv}`)
    })

}

if (datasources.getHistoryForSensorsB.lastUpdated) {
    handleSensorHistory(datasources.getHistoryForSensorsB.latestData());
}

if (!window.hasSubscribedToGetHistoryForSensorsB) {
    window.hasSubscribedToGetHistoryForSensorsB = true;
    datasources.getHistoryForSensorsB.latestData.subscribe(handleSensorHistory);
}

if (datasources.getAssets.lastUpdated) {
    handleAssets(datasources.getAssets.latestData());
}

if (!window.hasSubscribedToGetAssetsForChart) {
    window.hasSubscribedToGetAssetsForChart = true;
    datasources.getLocations.latestData.subscribe(handleAssets);
}

