const getSensorTypeById = (id) => {
    const types = datasources.getSensorTypes.latestData().results.DATA;
    return types.filter((t) => t.item_id === id)[0];
}

const getSensorSubscription = (topic) => {
    return window.sensorSubscriptions.filter((sub) => sub.topic === topic)[0];
}

const filterRulesBySensorId = (rules, id) => {
    try {
        for (let i = 0, len = rules.length; i < len; i++) {
            if (rules[i].sensor_id === id) {
                return rules[i];
            }
        }
    } catch (e) {
        return;
    }
}

const getRuleForSensorById = (alertConfigs, id) => {
    try {
        for (let i = 0, len = alertConfigs.length; i < len; i++) {
            const rules = JSON.parse(alertConfigs[i].rules);
            const sensorRules = filterRulesBySensorId(rules, id);
            if (sensorRules) {
                return sensorRules;
            }
        }
    } catch (e) {
        return;
    }
}

const getAlertConfigForSensorById = (id) => {
    if (datasources.getAlertConfigurations.lastUpdated) {
        try {
            const configs = datasources.getAlertConfigurations.latestData().results.DATA;
            return getRuleForSensorById(configs, id);
        } catch (e) {
            console.warn('Unable to get alert config for sensor with id', id, e);
        }
    }
}

const updateHumidityGauges = (sensorMsg) => {
    const val = sensorMsg.dataValue;
    const split = val.split('|');
    const humidity = split[0];
    const temp = split[1];
    updateSingleGauge(sensorMsg.sensorID, getLabelForSensor(sensorMsg), humidity, sensorMsg.messageDate, sensorMsg)
}

const getFillColorForSensor = (rule, value) => {
    if (rule) {
        try {
            const val = Number(value);
            const ruleThreshold = Number(rule.value);
            let crossedThreshold = false;
            switch (rule.operator) {
                case ruleOperators.GT:
                    crossedThreshold = val > ruleThreshold;
                    break;
                case ruleOperators.LT:
                    crossedThreshold = val < ruleThreshold;
                    break;
                case ruleOperators.EQ:
                    crossedThreshold = val === ruleThreshold;
                    break;
            }
            return crossedThreshold ? 'red' : 'green';
        } catch (e) {
            console.warn('Unable to get fill color for sensor', rule, value, e);
        }
    }
    return 'green';
}

const getGaugeLabel = (label, sensorMsg) => {
    try {
        switch (sensorMsg.plotLabels) {
            case "Celsius":
                return `${label} (*C)`;
            case "Humidity|Celsius":
                return `${label} (%)`;
        }
    } catch (e) {

    }
    return label;
}

const ruleOperators = {
    GT: 'GT',
    LT: 'LT',
    EQ: 'EQ'
}

const messageTimeClass = 'message-time';
const updateSingleGauge = (id, label, value, time, sensorMsg) => {
    console.log('single', sensorMsg)
    const mySensor = $(`#assetSensor${order}`);
    mySensor.show();
    mySensor.find(`.${messageTimeClass}`).remove();

    const msgTime = $('<div></div>');
    msgTime.addClass(messageTimeClass)
    msgTime.text(time);
    msgTime.css({
        'text-align': 'center',
        top: '-13px',
        position: 'relative',
        color: 'rgb(153, 153, 153)',
        'font-size': '.75rem'
    })
    mySensor.append(msgTime);
    const sensorRule = getAlertConfigForSensorById(id);
    const fillColor = getFillColorForSensor(sensorRule, value);
    datasources[`assetSensor${order}`].sendData({ label: getGaugeLabel(label, sensorMsg), value, fillColor });
}

const updateTempGauges = (sensorMsg, order) => {
    updateSingleGauge(sensorMsg.sensorID, getLabelForSensor(sensorMsg), sensorMsg.dataValue, sensorMsg.messageDate, sensorMsg);
}

const getSensorById = (id) => {
    const sensors = datasources.getSensors.latestData().results;
    for (let i = 0, len = sensors.length; i < len; i++) {
        if (sensors[i].name === id) {
            return sensors[i];
        }
    }
}

const getLabelForSensor = (sensorMsg) => {
    try {
        if (datasources.getSensors.lastUpdated) {
            const sensors = datasources.getSensors.latestData().results.DATA;
            for (let i = 0, len = sensors.length; i < len; i++) {
                if (sensors[i].sensor_id === sensorMsg.sensorID) {
                    return sensors[i].sensor_label;
                }
            }
        }
    } catch (e) {

    }
    return sensorMsg.sensorName;
}

const updateDoorToggle = (sensorMsg) => {
    let val = false;
    if (sensorMsg.dataValue === 'True') {
        val = true;
    }
    $('#assetSensor1').show();
    datasources.assetSensor1.sendData({ value: val });
}

const messageReceived = (msg) => {
    let parsedMsg;
    try {
        parsedMsg = JSON.parse(msg);
    } catch (e) {
        console.error('Failed to parse message', e);
    }
    if (parsedMsg && parsedMsg.sensor_message.sensorID) {
        try {

        const id = parsedMsg.sensor_message.sensorID;
        const sensor = getSensorById(id);
        const sensorType = getSensorTypeById(sensor.sensor_type_id);
        const alertConfig = getAlertConfigForSensorById(id);

        datasources.latestSensorData.sendData({
            ...datasources.latestSensorData.latestData(),
            [id]: {
                id,
                sensorType,
                sensor,
                message: parsedMsg,
                alertConfig
            }
        })
        } catch(e) {
            console.error('Unable to update latestSensorData', id, sensorType, sensor, e)
        }
    }
}

const setupMessaging = () => {
    return new Promise((resolve) => {
        if (!window.messagingObjectForSensors) {
            window.sensorSubscriptions = [];
            window.messagingObjectForSensors = CB_PORTAL.ClearBlade.Messaging({ useSSL: true }, resolve);
        } else {
            window.sensorSubscriptions.forEach((sub) => {
                try {
                    window.messagingObjectForSensors.unsubscribe(sub.topic, {});
                } catch (e) {
                    console.error('Unable to unsub from topics', e);
                }
            })

            window.sensorSubscriptions = [];
            resolve();
        }
    })
}

const messageHistoryCallback = (err, data) => {
    if (!err) {
        if (data && data[0]) {
            messageReceived(data[0].message);
        }
    }
}

const resetGauges = () => {
    datasources.latestSensorData.sendData({});
}

const updateGauges = (sensors) => {
    resetGauges();
    sensors.forEach((s) => {
        const sensorType = getSensorTypeById(s.sensor_type_id);
        const topicName = `sensor/${s.name}`;

        window.sensorSubscriptions.push({ topic: topicName });
        window.messagingObjectForSensors.subscribe(topicName, {}, messageReceived);
        CB_PORTAL.ClearBlade.MessagingStats().getMessageHistory(topicName, new Date().getTime(), 1, messageHistoryCallback)
    })
}

const assetId = this.widget.name;
datasources.getSensors.sendData({ edge_id: assetId }).then((data) => {
    let sensors;
    try {
        sensors = data.results;
    } catch (e) {
        console.error('error parsing sensors after selecting asset', e);
    }
    if (sensors && Array.isArray(sensors)) {

        // update the history line chart
        try {
            datasources.sensorHistoryIds.sendData(sensors.map((s) => s.name))
            if (datasources.sensorHistoryTimeRange.lastUpdated) {
                const timeRange = datasources.sensorHistoryTimeRange.latestData();
                datasources.getHistoryForSensors.sendData({
                    sensor_ids: datasources.sensorHistoryIds.latestData(),
                    startTime: timeRange.startDate.toISOString(),
                    endTime: timeRange.endDate.toISOString()
                })
            }
        } catch (e) {
            console.error('error parsing sensors after selecting asset', e);
        }
        console.log(`list of sensors for ${assetId}`, sensors);
        setupMessaging().then(() => {
            // update the 'latest data' gauges
            try {
                updateGauges(sensors);
            } catch (e) {
                console.error('error updating gauges', e);
            }
        })
    }
})
return this.widget;

