const AndDiv = `<div class="text-center text-muted mb-1 and-div">AND</div>`


const wordsFromOp = {
  GT: 'is above',
  LT: 'is below',
  EQ: 'is exactly',
};


function PropertyDiv(rule, sensorType) {
  return `
      <div class="input-group-prepend">
              <select class="custom-select" id="property">
                <option value="sensor_reading" ${'sensor_reading' === rule.property ? 'selected' : ''}>${sensorType.name}</option>
                <option value="battery_level" ${'battery_level' === rule.property ? 'selected' : ''}>Battery</option>
              </select>
      </div>
  `
}

// -- SensorRuleDiv --
function SensorRuleDiv(rule = {}) {
  if (!rule.sensor_id) {
    return `<div id="sensor-rule" class="input-group mb-2" />`
  }
  const sensor = datasources.getSensors.latestData().results.find((s) => s.name === rule.sensor_id) || {}
  const sensorType = datasources.getSensorTypes.latestData().results.DATA.find((s) => s.item_id === sensor.sensor_type_id) || {}
  if (rule.property === 'battery_level') {
    return `
    <div id="sensor-rule" class="input-group mb-2">
      <div class="input-group-prepend">
        ${PropertyDiv(rule, sensorType)}
      </div>

      <select class="custom-select d-none" id="operator">
          <option value="LT" selected>is below</option>
      </select>

      <div class="input-group-append">
        <div class="input-group-text">is below</div>
      </div>

      <input type="number" class="form-control" id="value" value="${rule.value}">

      <div class="input-group-append">
        <div class="input-group-text">percent</div>
      </div>
    </div>`
  }
  if (sensorType.data_type === 'int') {
    return `
      <div id="sensor-rule" class="input-group mb-2">
        <div class="input-group-prepend">
          ${PropertyDiv(rule, sensorType)}
        </div>

        <select class="custom-select" id="operator">
          ${Object.keys(wordsFromOp).map(op => (
        `<option value="${op}" ${op === rule.operator ? 'selected' : ''}>${wordsFromOp[op]}</option>`
      ))}
        </select>

        <input type="number" class="form-control" id="value" value="${rule.value}">

        <div class="input-group-append">
          <div class="input-group-text">${sensorType.attributes}</div>
        </div>
      </div>`
  } else {
    return `
      <div id="sensor-rule" class="input-group mb-2">
        <div class="input-group-prepend">
          ${PropertyDiv(rule, sensorType)}
        </div>

        <select class="custom-select" id="value">
          <option value="true" ${rule.value === true ? 'selected' : ''}>is open</option>
          <option value="false" ${rule.value === false ? 'selected' : ''}>is closed</option>
        </select>

        <input class="d-none" id="operator" value="EQ">
      </div>`
  }
}

// --- RULE DIV ---
function RuleDiv(rule = {}) {
  if (!datasources.getSensors.latestData()) return ''
  const sensor = datasources.getSensors.latestData().results.find((s) => s.name === rule.sensor_id) || {}
  let sensorOptions = datasources.getSensors.latestData().results

  if (window.edge_id) {
    sensorOptions = sensorOptions.filter((s) => s.edge_id === edge_id)
  }
  return `
<div class="rule">
  ${AndDiv}
  <div class="input-group">
    <select class="custom-select" id="sensor">
      <option selected>choose sensor...</option>
      ${sensorOptions.map((s) => (
      `<option value="${s.name}" ${s.name === rule.sensor_id ? 'selected' : ''}>${s.sensor_label || s.name}</option>`
    ))}
    </select>
    <div class="input-group-append">
      <div class="delete-alert-rule input-group-text"><i class="fa fa-trash" aria-hidden="true"></i></div>
    </div>
  </div>

  ${SensorRuleDiv(rule)}
</div>
`
}

const rules = this.value || []


function RulesArrDiv() {
  return `
  <div class="rules">
    ${rules.length === 0 ? RuleDiv() : rules.map(rule => RuleDiv(rule)).join('')}
  </div>

  <button type="button" id="add-rule-btn" class="btn btn-secondary my-3">+ Add rule</button>
 `}


if (!window.hasSetupAlertRules) {
  window.hasSetupAlertRules = true;
  $('body').on('click', '#add-rule-btn', (e) => {
    $(".rules").append(`
    ${RuleDiv()}`)
  })

  $('body').on('click', '.delete-alert-rule', (e) => {
    e.currentTarget.parentElement.parentElement.parentElement.remove()
  })

  $('body').on('change', '#sensor', (e) => {
    const sensor_id = $(e.target).val();
    $(e.target.parentElement.parentElement).find('#sensor-rule').replaceWith(SensorRuleDiv({ sensor_id }))
  })

  $('body').on('change', '#property', (e) => {
    const property = $(e.target).val();
    const ruleDiv = $(e.target.parentElement.parentElement.parentElement.parentElement)
    const sensor_id = ruleDiv.find('#sensor').val()
    ruleDiv.find('#sensor-rule').replaceWith(SensorRuleDiv({ sensor_id, property }))
  })
}

if (window.alertConfigId !== this.values.item_id) {
  window.alertConfigId = this.values.item_id  
  $("#my-HTML-rules").html(RulesArrDiv)
}

if (window.edge_id !== this.values.edge_id) {
  window.edge_id = this.values.edge_id  
  $("#my-HTML-rules").html(RulesArrDiv)
}

const button = `<div id="switch-to-add-sensor" class="btn btn-link">Add Alert Config</div>`

$("#my-rules-title").html(this.values.item_id ? `Update Alert Config ${button}` : `Add Alert Config`)


if(!window.hasAddedRulesTitleListeners) {
    window.hasAddedRulesTitleListeners = true
