if (!this.datasource.getAlertConfigurations) return

const wordsFromOp = {
  GT: 'is above',
  LT: 'is below',
  EQ: 'is exactly',
};

function AlertConfigSummary(alertConfig) {
    const rules = tryParse(alertConfig.rules)
    let text = rules && rules.map((rule) => RuleSummary(rule)).join(' and ')
    if(alertConfig.timeout) {
        text = text + ` for ${alertConfig.timeout} minutes`
    }
    return text
}

function RuleSummary(rule) {
    if(!datasources.getSensors.latestData() || !datasources.getSensorTypes.latestData()) return ''
    if(!rule) return ''
    const sensor = datasources.getSensors.latestData().results.find((s) => s.name === rule.sensor_id) || {}
    const sensorType = datasources.getSensorTypes.latestData().results.DATA.find((s) => s.item_id === sensor.sensor_type_id) || {}
    const propertyStr = rule.property === 'battery_level' ? 'Battery' : sensorType.name
    const wordsFromOp = {
        'GT': 'is above',
        'LT': 'is below',
        'EQ': 'is equal',
    }
    const operatorStr = (sensorType.data_type === 'int' || rule.property === 'battery_level') ? wordsFromOp[rule.operator] : ''
    const valueStr = (sensorType.data_type === 'int' || rule.property === 'battery_level') ? rule.value : (rule.value ? 'is open' : 'is closed')
    const attributeStr = rule.property === 'battery_level' ? '%' : sensorType.attributes
    return `"${sensor.sensor_label || sensor.name}" ${propertyStr && propertyStr.toLowerCase()} ${operatorStr} ${valueStr}${attributeStr}`
}

