const getDataFromSensors = () => {
    let rtn = {};
    const sensors = this.datasource;
    for (const id in sensors) {
        try {
            rtn = {
                ...rtn,
                [id]: Number(sensors[id].message.sensor_message.dataValue)
            }
        } catch (e) {
            console.warn(`Unable to get data for sensor '${id}'`, sensors, e);
        }
    }
    return rtn;
}

const getWidthPercentForSensors = (count) => {
    if (count === 1) {
        return '100';
    } else if (count === 2) {
        return '50';
    } else if (count === 3) {
        return '33';
    } else if (count >= 4) {
        return '25';
    }
    return '100';
}

const doesCrossAlertThreshold = (operator, threshold, value) => {
    switch (operator) {
        case 'EQ':
            return value == threshold;
        case 'GT':
            return value > threshold;
        case 'LT':
            return value < threshold;
        default:
            return false;
    }
}

const getProgressColor = (sensorInfo) => {
    try {
        if (sensorInfo.alertConfig) {
            return doesCrossAlertThreshold(sensorInfo.alertConfig.operator, Number(sensorInfo.alertConfig.value), Number(sensorInfo.message.sensor_message.dataValue)) ? 'red' : 'green';
        }
    } catch (e) { }
    return 'green';
}

