    let rtn = {};
    const sensors = this.datasource;
    const widthPercent = getWidthPercentForSensors(Object.keys(sensors).length);

    for (const id in sensors) {
        const progressColor = getProgressColor(sensors[id]);
        try {
            rtn = {
                ...rtn,
                [id]: {
                    name: id, dataType: 'number', display: true, key: id, inputType: 'RADIAL_GAUGE', widthPercent, progressColor
                }
            }
        } catch (e) {
            console.warn(`Unable to get field settings for '${id}'`, sensors, e);
        }
    }
    return rtn;
}

/**
 * @type {FormSourceObj}
 */
var formSourceObj = {
    // the initial data the form has when loaded
    data: getDataFromSensors(),

    // an object the same shape as fieldSettings, that is merged with the settings from the UI
    // useful for adding dynamic things like dropdown options from a collection
    overrideFieldSettings: getFieldSettingsFromSensors()
};
return formSourceObj
